mapper.delete:
DELETE FROM BD_BOARD
WHERE BRD_IDX in 

	#{item}

mapper.update:
UPDATE BD_BOARD
SET	TITLE = #{title}
	,CONTENT = #{content}
	,IMAGE_PATH = #{image_path}
	,UPT_DATE = GETDATE()
WHERE BRD_IDX = #{brd_idx}

mapper.select:
SELECT count(BRD_IDX) 
FROM BD_BOARD WITH(NOLOCK)
WHERE USE_YN = 'Y';
